import React, { Component } from "react";
import Select from "react-select";

import "./Currency.scss";
import axios from "axios";

class Currency extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      CurrencyRates: [],

      amountInput: "",
      fromSelect: ["1"],
      toSelect: ["1"],
      exchanged: null,
      oneToRate: null,
      oneFromRate: null,
      display: false
    };
    this.fromChange = this.fromChange.bind(this);
  }
  componentDidMount() {
    this.exchangeRates();
  }
  exchangeRates = () => {
    axios.get(`https://api.exchangeratesapi.io/latest`).then(res => {
      this.setState({
        data: res.data,
        CurrencyRates: res.data.rates
      });
    });
  };

  fromChange = e => {
    this.setState({
      fromSelect: e.target.value.split(",")
    });
  };
  toChange = e => {
    this.setState({
      toSelect: e.target.value.split(",")
    });
  };

  calc = () => {
    const { fromSelect, amountInput, toSelect } = this.state;
    if (fromSelect[0] === 1) {
      let calculated = amountInput * toSelect[0];
      this.setState({
        exchanged: calculated,
        display: true
      });
    } else if (toSelect[0] === 1) {
      let rate = 1 / fromSelect[0];
      let reverseRate = toSelect[0] / fromSelect[0];
      let calculated = amountInput * rate;
      this.setState({
        exchanged: calculated,
        oneFromRate: rate,
        oneToRate: reverseRate,
        display: true
      });
    } else if (fromSelect[0] || toSelect[0] !== 1) {
      let reverseRate = toSelect[0] / fromSelect[0];
      let rate = fromSelect[0] / toSelect[0];
      let calculated = amountInput * reverseRate;
      this.setState({
        exchanged: calculated,
        oneFromRate: reverseRate,
        oneToRate: rate,
        display: true
      });
    }
  };

  render() {
    const {
      CurrencyRates,
      amountInput,
      fromSelect,
      toSelect,
      exchanged,
      oneFromRate,
      oneToRate,
      display,
      data
    } = this.state;

    let rateArray = Object.entries(CurrencyRates);

    const sortArrays = rateArray.sort().map((items, i) => {
      const options = [{ value: [items[1], items[0]], label: items[0] }];
      return (
        // <option key={i} value={[items[1], items[0]]}>
        //   {items[0]}
        // </option>
        options
      );
    });
    console.log(sortArrays);

    return (
      <div>
        <Select
          value={fromSelect}
          onChange={this.fromChange}
          options={sortArrays}
        />
      </div>
    );
  }
}

export default Currency;
