import React, { Component } from "react";
import axios from "axios";
import { Bar, Line, Pie } from "react-chartjs-2";

class Chart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      chartData: {},
      stuff: []
    };
    this.getstuff = this.getstuff.bind(this);
  }

  componentDidMount() {
    this.chartchangeRates();
  }

  chartchangeRates = () => {
    axios.get(`https://api.exchangeratesapi.io/latest`).then(res => {
      this.setState({
        stuff: res.data.rates
      });
    });
  };
  getstuff = () => {
    let comeOn = this.state.stuff;
    let arrThem = Object.entries(comeOn);
    let in1 = arrThem.map(items => {
      return items[0];
    });
    let in2 = arrThem.map(items => {
      return items[1];
    });
    this.setState({
      chartData: {
        labels: in1,
        datasets: [
          {
            label: "Excahnge rates",
            data: in2,
            backgroundColor: ["rgba(3, 12, 34, 0.75)"]
          }
        ]
      }
    });
  };
  render() {
    console.log(this.props.name, this.props.exc);

    return (
      <div>
        <Bar data={this.state.chartData} options={{}} />
        <button onClick={this.getstuff}></button>
      </div>
    );
  }
}

export default Chart;
