import React, { Component } from "react";
import "./Chart.scss";
import { Bar, Line, Pie } from "react-chartjs-2";

class Chart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      chartData: {},
      graphVis: false,
      stuff: []
    };
    this.getstuff = this.getstuff.bind(this);
  }

  getstuff = () => {
    this.setState({
      chartData: {
        labels: this.props.name,
        datasets: [
          {
            label: `${this.props.date} ${this.props.based} Excahnge rates`,
            data: this.props.exc,
            backgroundColor: ["rgba(3, 12, 34, 0.75)"]
          }
        ]
      }
    });
  };
  render() {
    console.log(this.props.name, this.props.exc);

    return (
      <div>
        <button style={{ height: "8%", width: "20%" }} onClick={this.getstuff}>
          Current Rates
        </button>
        <Bar data={this.state.chartData} options={{}} />
      </div>
    );
  }
}

export default Chart;
