import React, { Component } from "react";
import { Doughnut } from "react-chartjs-2";

class Chart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      chartData: {
        labels: this.props.exc,
        datasets: [
          {
            label: "hello world",
            data: [],
            backgroundColor: []
          }
        ]
      }
    };
  }
  componentDidMount() {
    this.seperate();
  }
  seperate = () => {
    let ratesArr = this.props.exc.map(items => {
      return items[0];
    });
    let titleArr = this.props.exc.map(items => {
      return items[1];
    });
    this.setState({
      chartData: {
        labels: titleArr,
        datasets: [
          {
            label: "hello world",
            data: ratesArr,
            backgroundColor: []
          }
        ]
      }
    });
  };
  render() {
    console.log(this.state.chartData);
    return (
      <div>
        <Doughnut
          data={this.state.chartData}
          options={{
            maintainAspectRatio: false
          }}
        />
      </div>
    );
  }
}

export default Chart;
