import React, { Component } from "react";
import axios from "axios";

class Currency extends Component {
  constructor(props) {
    super(props);

    this.state = {
      CurrencyRates: [],
      amountInput: ""
    };
  }
  componentDidMount() {
    this.exchangeRates();
  }
  exchangeRates = () => {
    axios.get(`https://api.exchangeratesapi.io/latest`).then(res => {
      this.setState({
        CurrencyRates: res.data.rates
      });
    });
  };

  calc = (from, to, input, rate, frate) => {
    let calculated =
      from === "EUR"
        ? input * rate
        : to === "EUR"
        ? input * (1 / rate)
        : from || to !== "EUR"
        ? input * (rate / frate)
        : "hello";
    return calculated;
  };

  render() {
    const { CurrencyRates, amountInput } = this.state;
    const fromSelected = document.getElementById("xChange-from");
    const toSelected = document.getElementById("xChange-to");

    let toRate = +toSelected.value;
    console.log(toRate);

    let rateArray = Object.entries(CurrencyRates);

    const sortArrays = rateArray.sort().map((items, i) => {
      return (
        <option key={i} value={items[1]}>
          {items[0]}
        </option>
      );
    });
    console.log(sortArrays);

    return (
      <div>
        <div>
          <input
            value={amountInput}
            onChange={e => this.setState({ amountInput: e.target.value })}
          />
          <select id='xChange-from'> {sortArrays}</select>
          <select id='xChange-to'> {sortArrays}</select>
          <button></button>
        </div>
      </div>
    );
  }
}

export default Currency;
