import React, { Component } from "react";
import axios from "axios";

class Currency extends Component {
  constructor(props) {
    super(props);

    this.state = {
      CurrencyRates: [],
      amountInput: "",
      fromSelect: 1.8315,
      fromElement: "EUR",
      toSelect: 1.8315,
      exchanged: null,
      display: false
    };
    this.fromChange = this.fromChange.bind(this);
  }
  componentDidMount() {
    this.exchangeRates();
  }
  exchangeRates = () => {
    axios.get(`https://api.exchangeratesapi.io/latest`).then(res => {
      this.setState({
        CurrencyRates: res.data.rates
      });
    });
  };

  fromChange = e => {
    this.setState({
      fromSelect: e.target.value,
      fromElement: e.target.this.props.children
    });
  };
  toChange = e => {
    this.setState({
      toSelect: e.target.value
    });
  };

  calc = () => {
    const { fromSelect, amountInput, toSelect } = this.state;

    let calculated =
      fromSelect === 1
        ? amountInput * toSelect
        : toSelect === 1
        ? amountInput * (1 / fromSelect)
        : fromSelect || toSelect !== 1
        ? amountInput * (toSelect / fromSelect)
        : "hello";
    this.setState({ exchanged: calculated });
  };

  render() {
    const {
      CurrencyRates,
      amountInput,
      fromSelect,
      toSelect,
      exchanged,
      display
    } = this.state;

    let rateArray = Object.entries(CurrencyRates);

    const sortArrays = rateArray.sort().map((items, i) => {
      return (
        <option key={i} value={items[1]}>
          {items[0]}
        </option>
      );
    });
    console.log(sortArrays.value);

    return (
      <div>
        <div>
          <input
            value={amountInput}
            onChange={e => this.setState({ amountInput: e.target.value })}
          />
          <select
            children={this.state.fromElement}
            value={fromSelect}
            onChange={this.fromChange}
          >
            <option value='1'>EUR</option>
            {sortArrays}
          </select>
          <select value={toSelect} onChange={this.toChange}>
            <option value='1'>EUR</option>
            {sortArrays}
          </select>
          <button onClick={this.calc}></button>
        </div>
        <div className={display ? "visible" : "hidden"}>{exchanged}</div>
      </div>
    );
  }
}

export default Currency;
