import React, { Component } from "react";
import { Doughnut } from "react-chartjs-2";

class Chart extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    const { exc, date, based } = this.props;
    const mapForChart = exc.map(items => {
      const data = {
        labels: [items[1]],
        datasets: [
          {
            data: items[0],
            backgroundgroundColor: ["#FF6384"],
            hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"]
          }
        ]
      };
      return data;
    });
    return (
      <div>
        <Doughnut data={mapForChart} />
      </div>
    );
  }
}

export default Chart;
