import React, { Component } from "react";

import { Bar, Line, Pie } from "react-chartjs-2";

class Chart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      chartData: {}
    };
  }
  componentWillMount() {
    if (this.props.exc.length > 0) {
      return this.getstuff();
    }
  }
  getstuff() {
    this.setState({
      chartData: {
        labels: this.props.name,
        datasets: [
          {
            label: "Excahnge rates",
            data: this.props.exc,
            backgroundColor: ["rgba(3, 12, 34, 0.75)"]
          }
        ]
      }
    });
  }

  render() {
    console.log(this.props.name, this.props.exc);
    const data = this.state.chartData;
    return (
      <div>
        {this.props.exc && this.props.name != null ? (
          <Bar data={data} options={{}} redraw />
        ) : (
          ""
        )}
      </div>
    );
  }
}

export default Chart;
